// Code generated by NECPGAME backend agent. Enterprise-grade Combat service entry point.
// PERFORMANCE: Optimized server startup with performance monitoring and combat-specific tuning
// Issue: #2278 - Mass ogen Migration - 80+ Services Performance Upgrade

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/combat-service-go/internal/service"
	"necpgame/services/combat-service-go/pkg/api"
)

func main() {
	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Get configuration from environment with combat-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8083" // Combat service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9093" // Combat metrics port
	}

	// Initialize OpenTelemetry tracer for combat operations
	tracer := otel.Tracer("combat-service")

	// Initialize meter for combat metrics
	meter := otel.Meter("combat-service")

	// Create service instance with combat-specific optimizations
	svc, err := service.NewCombatService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	})
	if err != nil {
		logger.Fatal("Failed to create combat service", zap.Error(err))
	}

	// Create API handler with combat optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with combat-specific performance optimizations
	// PERFORMANCE: Tuned for high-frequency real-time combat operations
	srv := &http.Server{
		Addr: addr,
		Handler: api.NewServer(handler, api.WithTracer(tracer),
			api.WithMeter(meter)),
		ReadTimeout:       12 * time.Second,  // PERFORMANCE: Fast combat queries
		WriteTimeout:      18 * time.Second,  // PERFORMANCE: Allow time for complex combat responses
		IdleTimeout:       75 * time.Second,  // PERFORMANCE: Combat sessions can be active
		ReadHeaderTimeout: 2 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    1 << 20,          // PERFORMANCE: 1MB header limit
	}

	// Start main server with combat logging
	go func() {
		logger.Info("Starting combat service",
			zap.String("addr", addr),
			zap.String("performance_target", "P99 <25ms"),
			zap.String("concurrent_users", "10,000+"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Combat server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the combat server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down combat server...")

	// Give outstanding combat requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for active combat operations
	ctx, cancel := context.WithTimeout(context.Background(), 35*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Combat server forced to shutdown", zap.Error(err))
	}

	// Shutdown combat service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Combat service shutdown error", zap.Error(err))
	}

	logger.Info("Combat server exited gracefully")
}
