// Handler creates http.Handler with routing matching OpenAPI spec.
func Handler(si ServerInterface) http.Handler {
	return HandlerWithOptions(si, GorillaServerOptions{})
}

type GorillaServerOptions struct {
	BaseURL          string
	BaseRouter       *mux.Router
	Middlewares      []MiddlewareFunc
	ErrorHandlerFunc func(w http.ResponseWriter, r *http.Request, err error)
}

// HandlerFromMux creates http.Handler with routing matching OpenAPI spec based on the provided mux.
func HandlerFromMux(si ServerInterface, r *mux.Router) http.Handler {
	return HandlerWithOptions(si, GorillaServerOptions{
		BaseRouter: r,
	})
}

func HandlerFromMuxWithBaseURL(si ServerInterface, r *mux.Router, baseURL string) http.Handler {
	return HandlerWithOptions(si, GorillaServerOptions{
		BaseURL:    baseURL,
		BaseRouter: r,
	})
}

// HandlerWithOptions creates http.Handler with additional options
func HandlerWithOptions(si ServerInterface, options GorillaServerOptions) http.Handler {
	r := options.BaseRouter

	if r == nil {
		r = mux.NewRouter()
	}
	if options.ErrorHandlerFunc == nil {
		options.ErrorHandlerFunc = func(w http.ResponseWriter, r *http.Request, err error) {
			http.Error(w, err.Error(), http.StatusBadRequest)
		}
	}
	wrapper := ServerInterfaceWrapper{
		Handler:            si,
		HandlerMiddlewares: options.Middlewares,
		ErrorHandlerFunc:   options.ErrorHandlerFunc,
	}

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/catalog", wrapper.GetCosmeticCatalog).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/categories", wrapper.GetCosmeticCategories).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/equipped/{player_id}", wrapper.GetEquippedCosmetics).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/events/{player_id}", wrapper.GetCosmeticEvents).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/inventory/{player_id}", wrapper.GetCosmeticInventory).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/inventory/{player_id}/owned", wrapper.CheckCosmeticOwnership).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/purchase", wrapper.PurchaseCosmetic).Methods("POST")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/purchase/history/{player_id}", wrapper.GetPurchaseHistory).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/rarity/{rarity}", wrapper.GetCosmeticsByRarity).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/shop/daily", wrapper.GetDailyShop).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/shop/history", wrapper.GetShopHistory).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/{cosmetic_id}", wrapper.GetCosmeticDetails).Methods("GET")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/{cosmetic_id}/equip", wrapper.EquipCosmetic).Methods("POST")

	r.HandleFunc(options.BaseURL+"/monetization/cosmetic/{cosmetic_id}/unequip", wrapper.UnequipCosmetic).Methods("POST")

	return r
}
