// Code generated by NECPGAME backend agent. Enterprise-grade Difficulty Rating Service entry point.
// Issue: #142074659
// PERFORMANCE: Optimized server startup with performance monitoring

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/difficulty-service-go/internal/service"
)

func main() {
	// Initialize logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Get configuration from environment
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8085"
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = "0.0.0.0:9095"
	}

	// Initialize service
	cfg := service.Config{
		Logger:      logger,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	}

	svc, err := service.NewDifficultyService(cfg)
	if err != nil {
		logger.Fatal("Failed to initialize service", zap.Error(err))
	}

	// Create HTTP server
	mux := http.NewServeMux()

	// Health check endpoint
	mux.HandleFunc("/health", svc.HealthHandler)

	// API endpoints
	mux.HandleFunc("/api/v1/difficulty/player/", svc.PlayerRatingHandler)

	// Metrics endpoint
	mux.Handle("/metrics", svc.MetricsHandler())

	// Create server
	srv := &http.Server{
		Addr:         addr,
		Handler:      mux,
		ReadTimeout:  15 * time.Second,
		WriteTimeout: 15 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start metrics server in background
	go func() {
		metricsSrv := &http.Server{
			Addr:    metricsAddr,
			Handler: svc.MetricsHandler(),
		}
		if err := metricsSrv.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Error("Metrics server failed", zap.Error(err))
		}
	}()

	// Start server in background
	go func() {
		logger.Info("Starting difficulty rating service", zap.String("addr", addr))
		if err := srv.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("Server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit
	logger.Info("Shutting down server...")

	// Give outstanding requests 30 seconds to complete
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Server forced to shutdown", zap.Error(err))
	}

	logger.Info("Server exited")
}
