// Code generated by NECPGAME backend agent. Enterprise-grade Event Bus service entry point.
// PERFORMANCE: Optimized server startup with Kafka event processing and real-time messaging
// Issue: #2237 - Kafka Event-Driven Architecture

package main

import (
	"context"
	"log"
	"net/http"
	_ "net/http/pprof" // PERFORMANCE: pprof endpoint for profiling (Level 2 optimization)
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/event-bus-service-go/config"
	"necpgame/services/event-bus-service-go/internal/service"
)

func main() {
	// PERFORMANCE: GC tuning for event processing (Level 2 optimization)
	// Event bus handles high-volume real-time messaging
	if gcPercent := os.Getenv("GOGC"); gcPercent == "" {
		// debug.SetGCPercent(75) // Uncomment for production tuning
	}

	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Load configuration
	cfg := config.Load()

	// Get configuration from environment with event-bus-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8083" // Event bus service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9093" // Event bus metrics port
	}

	// PERFORMANCE: Profiling endpoint for real-time performance monitoring
	profilingAddr := os.Getenv("PPROF_ADDR")
	if profilingAddr == "" {
		profilingAddr = ":6063" // Event bus profiling port
	}

	// Initialize OpenTelemetry tracer for event processing
	tracer := otel.Tracer("event-bus-service")

	// Initialize meter for event metrics
	meter := otel.Meter("event-bus-service")

	// Create service instance with event-bus-specific optimizations
	svc, err := service.NewEventBusService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
		KafkaBrokers: os.Getenv("KAFKA_BROKERS"),
	})
	if err != nil {
		logger.Fatal("Failed to create event bus service", zap.Error(err))
	}

	// Create API handler with event processing optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with event-bus-specific performance optimizations
	// PERFORMANCE: Tuned for high-throughput event processing and real-time messaging
	srv := &http.Server{
		Addr: addr,
		Handler: handler,
		ReadTimeout:       30 * time.Second,  // PERFORMANCE: Allow time for complex event processing
		WriteTimeout:      30 * time.Second,  // PERFORMANCE: Allow time for event responses
		IdleTimeout:       120 * time.Second, // PERFORMANCE: Keep connections for event streams
		ReadHeaderTimeout: 5 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    2 << 20,          // PERFORMANCE: 2MB header limit for large events
	}

	// Start pprof profiling server for real-time performance monitoring
	go func() {
		logger.Info("Starting pprof profiling server", zap.String("addr", profilingAddr))
		if err := http.ListenAndServe(profilingAddr, nil); err != nil {
			logger.Error("Pprof server failed", zap.Error(err))
		}
	}()

	// Start main server with event-bus logging
	go func() {
		logger.Info("Starting event bus service",
			zap.String("addr", addr),
			zap.String("metrics_addr", metricsAddr),
			zap.String("pprof_addr", profilingAddr),
			zap.String("performance_target", "P99 <50ms"),
			zap.String("optimization_level", "Level 2 (Event-Driven)"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Event bus server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the event bus server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down event bus server...")

	// Give outstanding event requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for active event processing
	ctx, cancel := context.WithTimeout(context.Background(), 45*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Event bus server forced to shutdown", zap.Error(err))
	}

	// Shutdown event bus service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Event bus service shutdown error", zap.Error(err))
	}

	logger.Info("Event bus server exited gracefully")
}