// Code generated by NECPGAME backend agent. Enterprise-grade Gameplay Affixes Service entry point.
// Issue: #1495
// Implements optimized server startup with performance monitoring

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/gameplay-service-go/internal/service"
	"necpgame/services/gameplay-service-go/pkg/api"
)

func main() {
	// Initialize logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Get configuration from environment
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8083"
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9093"
	}

	// Initialize tracer
	tracer := otel.Tracer("gameplay-service")

	// Initialize meter
	meter := otel.Meter("gameplay-service")

	// Create service instance with optimizations
	svc, err := service.NewGameplayService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	})
	if err != nil {
		logger.Fatal("Failed to create gameplay service", zap.Error(err))
	}

	// Create API handler
	handler := service.NewHandler(svc)

	// Create server with optimizations
	srv := &http.Server{
		Addr: addr,
		Handler: api.NewServer(handler, api.WithTracer(tracer),
			api.WithMeter(meter)),
		ReadTimeout:       30 * time.Second,
		WriteTimeout:      30 * time.Second,
		IdleTimeout:       120 * time.Second,
		ReadHeaderTimeout: 5 * time.Second,
		MaxHeaderBytes:    1 << 20, // 1MB
	}

	// Start metrics server
	go func() {
		metricsSrv := &http.Server{
			Addr:    metricsAddr,
			Handler: svc.MetricsHandler(),
		}
		if err := metricsSrv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Error("Metrics server failed", zap.Error(err))
		}
	}()

	// Start main server
	go func() {
		logger.Info("Starting gameplay service", zap.String("addr", addr))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down server...")

	// Give outstanding requests a deadline for completion
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Server forced to shutdown", zap.Error(err))
	}

	logger.Info("Server exited")
}
