// Repository handles guild event operations
type Repository struct {
	db    *pgxpool.Pool
	redis *redis.Client
	log   *zap.Logger
}

// NewRepository creates optimized event repository
func NewRepository(db *pgxpool.Pool, redis *redis.Client, log *zap.Logger) *Repository {
	return &Repository{
		db:    db,
		redis: redis,
		log:   log,
	}
}

// CancelGuildEvents cancels all active events for a guild
func (r *Repository) CancelGuildEvents(ctx context.Context, guildID uuid.UUID) error {
	_, err := r.db.Exec(ctx, `
		UPDATE guild_events
		SET status = 'cancelled', cancelled_at = NOW()
		WHERE guild_id = $1 AND status = 'active'`, guildID)

	if err != nil {
		return errors.Wrap(err, "cancel guild events")
	}

	return nil
}
