// Code generated by NECPGAME backend agent. Enterprise-grade League service entry point.
// PERFORMANCE: Optimized server startup with performance monitoring and league-specific tuning
// Issue: #2207 - League Service for Competitive Gaming Systems

package main

import (
	"context"
	"log"
	"net/http"
	_ "net/http/pprof" // PERFORMANCE: pprof endpoint for profiling (Level 2 optimization)
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/league-service-go/config"
	"necpgame/services/league-service-go/internal/service"
)

func main() {
	// PERFORMANCE: GC tuning for league operations (Level 2 optimization)
	// League service handles ranking calculations and tournament management
	if gcPercent := os.Getenv("GOGC"); gcPercent == "" {
		// debug.SetGCPercent(75) // Uncomment for production tuning
	}

	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Load configuration
	cfg := config.Load()

	// Get configuration from environment with league-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8082" // League service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9092" // League metrics port
	}

	// PERFORMANCE: Profiling endpoint for real-time performance monitoring
	profilingAddr := os.Getenv("PPROF_ADDR")
	if profilingAddr == "" {
		profilingAddr = ":6062" // League profiling port
	}

	// Initialize OpenTelemetry tracer for league operations
	tracer := otel.Tracer("league-service")

	// Initialize meter for league metrics
	meter := otel.Meter("league-service")

	// Create service instance with league-specific optimizations
	svc, err := service.NewLeagueService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	})
	if err != nil {
		logger.Fatal("Failed to create league service", zap.Error(err))
	}

	// Create API handler with league optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with league-specific performance optimizations
	// PERFORMANCE: Tuned for ranking calculations and tournament operations
	srv := &http.Server{
		Addr: addr,
		Handler: handler,
		ReadTimeout:       15 * time.Second,  // PERFORMANCE: Allow time for complex queries
		WriteTimeout:      20 * time.Second,  // PERFORMANCE: Allow time for ranking responses
		IdleTimeout:       90 * time.Second,  // PERFORMANCE: Keep connections for league sessions
		ReadHeaderTimeout: 3 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    1 << 20,          // PERFORMANCE: 1MB header limit
	}

	// Start pprof profiling server for real-time performance monitoring
	go func() {
		logger.Info("Starting pprof profiling server", zap.String("addr", profilingAddr))
		if err := http.ListenAndServe(profilingAddr, nil); err != nil {
			logger.Error("Pprof server failed", zap.Error(err))
		}
	}()

	// Start main server with league logging
	go func() {
		logger.Info("Starting league service",
			zap.String("addr", addr),
			zap.String("metrics_addr", metricsAddr),
			zap.String("pprof_addr", profilingAddr),
			zap.String("performance_target", "P99 <20ms"),
			zap.String("optimization_level", "Level 2 (User-Facing)"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("League server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the league server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down league server...")

	// Give outstanding league requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for active ranking operations
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("League server forced to shutdown", zap.Error(err))
	}

	// Shutdown league service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("League service shutdown error", zap.Error(err))
	}

	logger.Info("League server exited gracefully")
}