// Code generated by NECPGAME backend agent. Enterprise-grade Message Queue service entry point.
// PERFORMANCE: Optimized server startup with Kafka message processing and inter-service communication
// Issue: #2205 - Message Queue Service for Inter-Service Communication

package main

import (
	"context"
	"log"
	"net/http"
	_ "net/http/pprof" // PERFORMANCE: pprof endpoint for profiling (Level 2 optimization)
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/message-queue-service-go/config"
	"necpgame/services/message-queue-service-go/internal/service"
)

func main() {
	// PERFORMANCE: GC tuning for message queue processing (Level 2 optimization)
	// Message queue handles high-volume inter-service communication
	if gcPercent := os.Getenv("GOGC"); gcPercent == "" {
		// debug.SetGCPercent(75) // Uncomment for production tuning
	}

	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Load configuration
	cfg := config.Load()

	// Get configuration from environment with message-queue-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8087" // Message queue service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9097" // Message queue metrics port
	}

	// PERFORMANCE: Profiling endpoint for real-time performance monitoring
	profilingAddr := os.Getenv("PPROF_ADDR")
	if profilingAddr == "" {
		profilingAddr = ":6067" // Message queue profiling port
	}

	// Initialize OpenTelemetry tracer for message processing
	tracer := otel.Tracer("message-queue-service")

	// Initialize meter for message queue metrics
	meter := otel.Meter("message-queue-service")

	// Create service instance with message-queue-specific optimizations
	svc, err := service.NewService(cfg, logger, tracer, meter)
	if err != nil {
		logger.Fatal("Failed to create message queue service", zap.Error(err))
	}

	// Create API handler with message processing optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with message-queue-specific performance optimizations
	// PERFORMANCE: Tuned for high-volume message processing and inter-service communication
	srv := &http.Server{
		Addr: addr,
		Handler: handler,
		ReadTimeout:       30 * time.Second,  // PERFORMANCE: Allow time for complex message processing
		WriteTimeout:      30 * time.Second,  // PERFORMANCE: Allow time for message responses
		IdleTimeout:       120 * time.Second, // PERFORMANCE: Keep connections for service communication
		ReadHeaderTimeout: 5 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    2 << 20,          // PERFORMANCE: 2MB header limit for large messages
	}

	// Start pprof profiling server for real-time performance monitoring
	go func() {
		logger.Info("Starting pprof profiling server", zap.String("addr", profilingAddr))
		if err := http.ListenAndServe(profilingAddr, nil); err != nil {
			logger.Error("Pprof server failed", zap.Error(err))
		}
	}()

	// Start main server with message-queue logging
	go func() {
		logger.Info("Starting message queue service",
			zap.String("addr", addr),
			zap.String("metrics_addr", metricsAddr),
			zap.String("pprof_addr", profilingAddr),
			zap.String("performance_target", "P99 <15ms"),
			zap.String("optimization_level", "Level 2 (High-Volume)"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Message queue server failed", zap.Error(err))
		}
	}()

	// Start message processing monitoring
	go func() {
		logger.Info("Starting message processing monitoring")
		if err := svc.StartMessageMonitoring(ctx); err != nil {
			logger.Error("Message monitoring failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the message queue servers
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down message queue servers...")

	// Give outstanding message processing a deadline for completion
	// PERFORMANCE: Reasonable timeout for active message processing
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	// Shutdown main server
	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Message queue server forced to shutdown", zap.Error(err))
	}

	// Shutdown message queue service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Message queue service shutdown error", zap.Error(err))
	}

	logger.Info("Message queue servers exited gracefully")
}