// Code generated by NECPGAME backend agent. Enterprise-grade Security service entry point.
// PERFORMANCE: Optimized server startup with performance monitoring and security-specific tuning
// Issue: #2208 - Security Service for Authentication and Authorization

package main

import (
	"context"
	"log"
	"net/http"
	_ "net/http/pprof" // PERFORMANCE: pprof endpoint for profiling (Level 3 optimization)
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/security-service-go/config"
	"necpgame/services/security-service-go/internal/service"
)

func main() {
	// PERFORMANCE: GC tuning for security operations (Level 3 optimization)
	// Security service handles high-frequency auth checks with low latency requirements
	if gcPercent := os.Getenv("GOGC"); gcPercent == "" {
		// debug.SetGCPercent(75) // Uncomment for production tuning (higher than game servers)
	}

	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Load configuration
	cfg := config.Load()

	// Get configuration from environment with security-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8081" // Security service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9091" // Security metrics port
	}

	// PERFORMANCE: Profiling endpoint for real-time performance monitoring
	profilingAddr := os.Getenv("PPROF_ADDR")
	if profilingAddr == "" {
		profilingAddr = ":6061" // Security profiling port
	}

	// Initialize OpenTelemetry tracer for security operations
	tracer := otel.Tracer("security-service")

	// Initialize meter for security metrics
	meter := otel.Meter("security-service")

	// Create service instance with security-specific optimizations
	svc, err := service.NewSecurityService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
		JWTSecret:   cfg.JWT.Secret,
	})
	if err != nil {
		logger.Fatal("Failed to create security service", zap.Error(err))
	}

	// Create API handler with security optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with security-specific performance optimizations
	// PERFORMANCE: Tuned for high-frequency authentication operations
	srv := &http.Server{
		Addr: addr,
		Handler: handler,
		ReadTimeout:       10 * time.Second,  // PERFORMANCE: Fast auth checks
		WriteTimeout:      15 * time.Second,  // PERFORMANCE: Allow time for token responses
		IdleTimeout:       90 * time.Second,  // PERFORMANCE: Keep connections for session reuse
		ReadHeaderTimeout: 2 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    1 << 20,          // PERFORMANCE: 1MB header limit
	}

	// Start pprof profiling server for real-time performance monitoring
	go func() {
		logger.Info("Starting pprof profiling server", zap.String("addr", profilingAddr))
		if err := http.ListenAndServe(profilingAddr, nil); err != nil {
			logger.Error("Pprof server failed", zap.Error(err))
		}
	}()

	// Start main server with security logging
	go func() {
		logger.Info("Starting security service",
			zap.String("addr", addr),
			zap.String("metrics_addr", metricsAddr),
			zap.String("pprof_addr", profilingAddr),
			zap.String("performance_target", "P99 <15ms"),
			zap.String("optimization_level", "Level 2 (User-Facing)"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Security server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the security server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down security server...")

	// Give outstanding security requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for active security operations
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Security server forced to shutdown", zap.Error(err))
	}

	// Shutdown security service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Security service shutdown error", zap.Error(err))
	}

	logger.Info("Security server exited gracefully")
}