// Code generated by NECPGAME backend agent. Enterprise-grade Tournament service entry point.
// PERFORMANCE: Optimized server startup with performance monitoring and tournament-specific tuning
// Issue: #2278 - Mass ogen Migration - 80+ Services Performance Upgrade

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/tournament-service-go/internal/service"
	"necpgame/services/tournament-service-go/pkg/api"
)

func main() {
	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Get configuration from environment with tournament-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8081" // Tournament service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9091" // Tournament metrics port
	}

	// Initialize OpenTelemetry tracer for tournament operations
	tracer := otel.Tracer("tournament-service")

	// Initialize meter for tournament metrics
	meter := otel.Meter("tournament-service")

	// Create service instance with tournament-specific optimizations
	svc, err := service.NewTournamentService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	})
	if err != nil {
		logger.Fatal("Failed to create tournament service", zap.Error(err))
	}

	// Create API handler with tournament optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with tournament-specific performance optimizations
	// PERFORMANCE: Tuned for high-frequency tournament operations
	srv := &http.Server{
		Addr: addr,
		Handler: api.NewServer(handler, api.WithTracer(tracer),
			api.WithMeter(meter)),
		ReadTimeout:       15 * time.Second,  // PERFORMANCE: Shorter for tournament queries
		WriteTimeout:      20 * time.Second,  // PERFORMANCE: Allow time for complex responses
		IdleTimeout:       90 * time.Second,  // PERFORMANCE: Tournament sessions can be longer
		ReadHeaderTimeout: 3 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    1 << 20,          // PERFORMANCE: 1MB header limit
	}

	// Start main server with tournament logging
	go func() {
		logger.Info("Starting tournament service",
			zap.String("addr", addr),
			zap.String("performance_target", "P99 <30ms"),
			zap.String("concurrent_users", "100,000+"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Tournament server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the tournament server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down tournament server...")

	// Give outstanding tournament requests a deadline for completion
	// PERFORMANCE: Longer timeout for tournament operations that may be in progress
	ctx, cancel := context.WithTimeout(context.Background(), 45*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Tournament server forced to shutdown", zap.Error(err))
	}

	// Shutdown tournament service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Tournament service shutdown error", zap.Error(err))
	}

	logger.Info("Tournament server exited gracefully")
}
