// Code generated by NECPGAME backend agent. Enterprise-grade Trading service entry point.
// PERFORMANCE: Optimized server startup with performance monitoring and trading-specific tuning
// Issue: #2278 - Mass ogen Migration - 80+ Services Performance Upgrade

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/trading-service-go/internal/service"
	"necpgame/services/trading-service-go/pkg/api"
)

func main() {
	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Get configuration from environment with trading-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8082" // Trading service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9092" // Trading metrics port
	}

	// Initialize OpenTelemetry tracer for trading operations
	tracer := otel.Tracer("trading-service")

	// Initialize meter for trading metrics
	meter := otel.Meter("trading-service")

	// Create service instance with trading-specific optimizations
	svc, err := service.NewTradingService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
	})
	if err != nil {
		logger.Fatal("Failed to create trading service", zap.Error(err))
	}

	// Create API handler with trading optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with trading-specific performance optimizations
	// PERFORMANCE: Tuned for high-frequency P2P trading operations
	srv := &http.Server{
		Addr: addr,
		Handler: api.NewServer(handler, api.WithTracer(tracer),
			api.WithMeter(meter)),
		ReadTimeout:       10 * time.Second,  // PERFORMANCE: Fast trading queries
		WriteTimeout:      15 * time.Second,  // PERFORMANCE: Allow time for negotiations
		IdleTimeout:       60 * time.Second,  // PERFORMANCE: Trading sessions can be active
		ReadHeaderTimeout: 2 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    1 << 20,          // PERFORMANCE: 1MB header limit
	}

	// Start main server with trading logging
	go func() {
		logger.Info("Starting trading service",
			zap.String("addr", addr),
			zap.String("performance_target", "P99 <20ms"),
			zap.String("concurrent_users", "50,000+"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("Trading server failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the trading server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down trading server...")

	// Give outstanding trading requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for trading operations
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Trading server forced to shutdown", zap.Error(err))
	}

	// Shutdown trading service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("Trading service shutdown error", zap.Error(err))
	}

	logger.Info("Trading server exited gracefully")
}
