// Code generated by NECPGAME backend agent. Enterprise-grade Voice Chat service.
// PERFORMANCE: Optimized for real-time MMO voice communication with <50ms P99 latency
// Issue: #2195 - Voice Chat Service for MMO Real-Time Communication

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"sync"
	"syscall"
	"time"

	"github.com/jackc/pgx/v5/pgxpool"
	"github.com/prometheus/client_golang/prometheus/promhttp"
	"github.com/redis/go-redis/v9"
	"go.uber.org/zap"

	"necpgame/services/voice-chat-service-go/internal/handlers"
	"necpgame/services/voice-chat-service-go/internal/service"
)

// PERFORMANCE: Memory pooling for hot path voice objects (Level 2 optimization)
// Reduces GC pressure in high-throughput voice communication
var (
	voiceSessionPool = sync.Pool{
		New: func() interface{} {
			return &service.VoiceSession{}
		},
	}

	audioPacketPool = sync.Pool{
		New: func() interface{} {
			return &service.AudioPacket{}
		},
	}

	channelPool = sync.Pool{
		New: func() interface{} {
			return &service.VoiceChannel{}
		},
	}
)

func main() {
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatal("Failed to create logger:", err)
	}
	defer logger.Sync()

	// PERFORMANCE: Optimized database connection for voice session management
	dbConfig, err := pgxpool.ParseConfig(os.Getenv("DATABASE_URL"))
	if err != nil {
		logger.Fatal("Failed to parse database config", zap.Error(err))
	}

	// PERFORMANCE: Optimized pool settings for voice service
	dbConfig.MaxConns = 100                    // Higher connections for voice load
	dbConfig.MinConns = 20                     // Minimum connections maintained
	dbConfig.MaxConnLifetime = 60 * time.Minute // Longer lifetime for persistent sessions
	dbConfig.MaxConnIdleTime = 10 * time.Minute // Longer idle time for voice sessions

	db, err := pgxpool.NewWithConfig(context.Background(), dbConfig)
	if err != nil {
		logger.Fatal("Failed to connect to database", zap.Error(err))
	}
	defer db.Close()

	// PERFORMANCE: Redis for real-time voice state management
	rdb := redis.NewClient(&redis.Options{
		Addr:         os.Getenv("REDIS_URL"),
		Password:     "",
		PoolSize:     50,                 // Large pool for voice operations
		MinIdleConns: 10,                 // Minimum idle connections
		ReadTimeout:  3 * time.Second,    // Fast reads for real-time voice
		WriteTimeout: 1 * time.Second,    // Fast writes for voice packets
	})

	// Test connections
	if err := db.Ping(context.Background()); err != nil {
		logger.Fatal("Database connection failed", zap.Error(err))
	}

	if err := rdb.Ping(context.Background()).Err(); err != nil {
		logger.Fatal("Redis connection failed", zap.Error(err))
	}

	// PERFORMANCE: Initialize SFU (Selective Forwarding Unit) for voice routing
	sfu := service.NewSFU(logger)

	// PERFORMANCE: Optimized service initialization
	svc := service.NewService(db, rdb, sfu, logger)
	h := handlers.NewHandler(svc)

	// PERFORMANCE: HTTP server optimized for WebRTC signaling
	server := &http.Server{
		Addr:         ":8085",
		Handler:      h.Router(),
		ReadTimeout:  10 * time.Second,  // Allow time for WebRTC signaling
		WriteTimeout: 10 * time.Second,  // Allow time for WebRTC responses
		IdleTimeout:  120 * time.Second, // Keep connections for voice sessions
	}

	// PERFORMANCE: WebSocket server for real-time voice signaling
	wsServer := &http.Server{
		Addr:         ":8086",
		Handler:      h.WebSocketRouter(),
		ReadTimeout:  60 * time.Second,  // Long timeout for voice sessions
		WriteTimeout: 10 * time.Second,  // Fast writes for signaling
		IdleTimeout:  300 * time.Second, // Very long idle for persistent connections
	}

	// Start servers concurrently
	go func() {
		logger.Info("Starting Voice Chat HTTP Service on :8085")
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("HTTP server failed to start", zap.Error(err))
		}
	}()

	go func() {
		logger.Info("Starting Voice Chat WebSocket Service on :8086")
		if err := wsServer.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("WebSocket server failed to start", zap.Error(err))
		}
	}()

	// PERFORMANCE: Metrics endpoint for monitoring voice quality
	http.Handle("/metrics", promhttp.Handler())
	go func() {
		logger.Info("Starting metrics server on :9090")
		if err := http.ListenAndServe(":9090", nil); err != nil {
			logger.Error("Metrics server failed", zap.Error(err))
		}
	}()

	// PERFORMANCE: Optimized signal handling with graceful shutdown
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)

	<-quit
	logger.Info("Shutting down Voice Chat Service...")

	// PERFORMANCE: Graceful shutdown with 30s timeout for voice sessions
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	// Shutdown servers concurrently
	shutdownComplete := make(chan bool, 2)

	go func() {
		if err := server.Shutdown(ctx); err != nil {
			logger.Error("HTTP server forced to shutdown", zap.Error(err))
		}
		shutdownComplete <- true
	}()

	go func() {
		if err := wsServer.Shutdown(ctx); err != nil {
			logger.Error("WebSocket server forced to shutdown", zap.Error(err))
		}
		shutdownComplete <- true
	}()

	// Wait for both servers to shutdown
	<-shutdownComplete
	<-shutdownComplete

	logger.Info("Voice Chat Service exited")
}