// Code generated by NECPGAME backend agent. Enterprise-grade World Events service entry point.
// PERFORMANCE: Optimized server startup with Kafka event processing and world event scheduling
// Issue: #2224 - Реализация world-events-service-go

package main

import (
	"context"
	"log"
	"net/http"
	_ "net/http/pprof" // PERFORMANCE: pprof endpoint for profiling (Level 2 optimization)
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/go-faster/errors"
	"go.opentelemetry.io/otel"
	"go.opentelemetry.io/otel/metric"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"

	"necpgame/services/world-events-service-go/config"
	"necpgame/services/world-events-service-go/internal/service"
)

func main() {
	// PERFORMANCE: GC tuning for world event processing (Level 2 optimization)
	// World events service handles scheduled and dynamic world events
	if gcPercent := os.Getenv("GOGC"); gcPercent == "" {
		// debug.SetGCPercent(75) // Uncomment for production tuning
	}

	// Initialize structured logger
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatalf("Failed to initialize logger: %v", err)
	}
	defer logger.Sync()

	// Load configuration
	cfg := config.Load()

	// Get configuration from environment with world-events-specific defaults
	addr := os.Getenv("ADDR")
	if addr == "" {
		addr = "0.0.0.0:8084" // World events service port
	}

	metricsAddr := os.Getenv("METRICS_ADDR")
	if metricsAddr == "" {
		metricsAddr = ":9094" // World events metrics port
	}

	// PERFORMANCE: Profiling endpoint for real-time performance monitoring
	profilingAddr := os.Getenv("PPROF_ADDR")
	if profilingAddr == "" {
		profilingAddr = ":6065" // World events profiling port
	}

	// Initialize OpenTelemetry tracer for world event processing
	tracer := otel.Tracer("world-events-service")

	// Initialize meter for world event metrics
	meter := otel.Meter("world-events-service")

	// Create service instance with world-events-specific optimizations
	svc, err := service.NewWorldEventsService(service.Config{
		Logger:      logger,
		Tracer:      tracer,
		Meter:       meter,
		DatabaseURL: os.Getenv("DATABASE_URL"),
		RedisURL:    os.Getenv("REDIS_URL"),
		KafkaBrokers: os.Getenv("KAFKA_BROKERS"),
	})
	if err != nil {
		logger.Fatal("Failed to create world events service", zap.Error(err))
	}

	// Create API handler with world event processing optimizations
	handler := service.NewHandler(svc)

	// Create HTTP server with world-events-specific performance optimizations
	// PERFORMANCE: Tuned for scheduled world events and real-time notifications
	srv := &http.Server{
		Addr: addr,
		Handler: handler,
		ReadTimeout:       30 * time.Second,  // PERFORMANCE: Allow time for complex event queries
		WriteTimeout:      30 * time.Second,  // PERFORMANCE: Allow time for event responses
		IdleTimeout:       120 * time.Second, // PERFORMANCE: Keep connections for event streams
		ReadHeaderTimeout: 5 * time.Second,   // PERFORMANCE: Faster header processing
		MaxHeaderBytes:    2 << 20,          // PERFORMANCE: 2MB header limit for large events
	}

	// Start pprof profiling server for real-time performance monitoring
	go func() {
		logger.Info("Starting pprof profiling server", zap.String("addr", profilingAddr))
		if err := http.ListenAndServe(profilingAddr, nil); err != nil {
			logger.Error("Pprof server failed", zap.Error(err))
		}
	}()

	// Start main server with world-events logging
	go func() {
		logger.Info("Starting world events service",
			zap.String("addr", addr),
			zap.String("metrics_addr", metricsAddr),
			zap.String("pprof_addr", profilingAddr),
			zap.String("performance_target", "P99 <50ms"),
			zap.String("optimization_level", "Level 2 (Event-Driven)"))
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			logger.Fatal("World events server failed", zap.Error(err))
		}
	}()

	// Start world event scheduler
	go func() {
		logger.Info("Starting world event scheduler")
		if err := svc.StartScheduler(ctx); err != nil {
			logger.Error("World event scheduler failed", zap.Error(err))
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the world events server
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("Shutting down world events server...")

	// Give outstanding world event requests a deadline for completion
	// PERFORMANCE: Reasonable timeout for active world event processing
	ctx, cancel := context.WithTimeout(context.Background(), 45*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("World events server forced to shutdown", zap.Error(err))
	}

	// Shutdown world events service with cleanup
	if err := svc.Shutdown(ctx); err != nil {
		logger.Error("World events service shutdown error", zap.Error(err))
	}

	logger.Info("World events server exited gracefully")
}